            SLog.Write(ex);
        }
    }

    public async void Imagebase64(string src)
    {
        if (String.IsNullOrEmpty(src))
        {
            return;
        }
        Model.CoverImage = await UploaderService.UploadFile(src);
        StateHasChanged();
    }

    public class Deadlines
    {
        public string Date1 { get; set; }
        public string Date2 { get; set; }
        public string Date3 { get; set; }
        public string Date4 { get; set; }
        public string Date5 { get; set; }
        public string Date6 { get; set; }
    }

    public Deadlines Dates = new Deadlines();
    public async void SubmitModel()
    {
        try
        {
            bool Name = await ClientService.CheckName(Model.Name);
            var Object = await JSRuntime.InvokeAsync<object>("getDateValues");
            Dates = JsonConvert.DeserializeObject<Deadlines>(Object.ToString());
            Model.Description = await JSRuntime.InvokeAsync<string>("getEditorValue");
            Model.RegistrationStart = Convert.ToDateTime(Dates.Date1);
            Model.RegistrationEnd = Convert.ToDateTime(Dates.Date2);
            Model.EventStart = Convert.ToDateTime(Dates.Date3);
            Model.EventEnd = Convert.ToDateTime(Dates.Date4);

            if (Model.Description == "<p><br></p>")
            {
                ToastService.ShowWarning("Please Try Again :)", "Oops! Empty Description");
                return;
            }
            if (Name)
            {
                ToastService.ShowError("Events Title is Already taken", " ");
            }
            else
            {
                HttpResponseMessage response = await ClientService.PostAsync(Model);
                if (response.IsSuccessStatusCode)
                {
                    ToastService.ShowSuccess("Event created successfully!", "");
                    navigationManager.NavigateTo("/events");
                }
                else
                {
                    SLog.Write("Event Creation  Failed");
                    ToastService.ShowError(response.ReasonPhrase, "Server Posting Failed");

                }

            }

        }
        catch (Exception ex)
        {
            SLog.Write(ex);
            ToastService.ShowError(ex.Message, "Event Failed Exception");
        }
    }

    public void ShowRoundForm()
    {
        RoundContainer = "d-block";
        Round = new Round();
        Round.ClearRoundValue();
        StateHasChanged();
    }

    public void CancelRound()
    {
        RoundContainer = "d-none";
        RoundCard = "d-none";
        Round.ClearRoundValue();
    }

    public async void SaveRound()
    {
        var Object = await JSRuntime.InvokeAsync<object>("getDateValues");
        Dates = Newtonsoft.Json.JsonConvert.DeserializeObject<Deadlines>(Object.ToString());
        Round.StartDate = Convert.ToDateTime(Dates.Date5);
        Round.EndDate = Convert.ToDateTime(Dates.Date6);
        Model.Rounds.Add(Round);
        RoundCard = "d-block";
        RoundContainer = "d-none";
        StateHasChanged();
    }


